import React, { useEffect, useState } from "react";
import Cookies from "js-cookie";
import { sideMenus } from "../../constants/sideMenus";
import PropTypes from "prop-types";
import { useLocation } from "react-router-dom";

export default function BasicBox({ children, ...props }) {
  const { overflow } = props;
  //localstorage 로그인 정보 가져오기
  const [user, setUser] = useState(null);
  const [showDelayedContent, setShowDelayedContent] = useState(false);
  useEffect(() => {
    const storedUser = Cookies.get("user"); // 쿠키에서 'user' 쿠키 가져오기
    if (storedUser) {
      setUser(JSON.parse(storedUser)); // 쿠키에서 가져온 문자열을 객체로 변환하여 상태 설정
    }
    setTimeout(() => {
      setShowDelayedContent(true);
    }, 100);
  }, []);

  //페이지 타이틀과 페이지 명 가져오기
  const location = useLocation();
  const URL = location.pathname;
  var modifiedUrls = () => {
    var parts = URL.split("/");
    if (parts.length > 2) {
      parts.splice(2, 1);
    }
    return parts.join("/");
  };
  const targetPath = modifiedUrls();
  let foundMenu = null;
  sideMenus.forEach((menuGroup) => {
    menuGroup.menus.forEach((menu) => {
      if (menu.href === targetPath) {
        foundMenu = { title: menuGroup.title, name: menu.name };
      }
    });
  });

  return (
    <>
      <div className="fontA text-3xl pt-10 pl-10">
        {foundMenu ? (
          <>
            {foundMenu.title}{" "}
            <span className="text-lg fontA">
              {" "}
              {">"} {foundMenu.name}
            </span>
          </>
        ) : null}
      </div>

      <div className="relative w-full justify-center flex px-10 py-12">
        {showDelayedContent && !user ? (
          <div className="absolute w-full h-full justify-center items-center flex">
            <div className="fontA text-5xl">로그인 후 사용이 가능합니다.</div>
          </div>
        ) : null}
        {showDelayedContent && (
          <div
            className={`min-w-[50vw] ${
              overflow ? "overflow-hidden" : ""
            } relative border-4 rounded-md shadow-2xl border-[#5272F2] px-10 bg-[#FAFBFE]  ${
              !user ? "opacity-30 pointer-events-none" : ""
            }`}
          >
            {children}
          </div>
        )}
      </div>
    </>
  );
}

BasicBox.propTypes = {
  overflow: PropTypes.bool,
};

BasicBox.defaultProps = {
  overflow: false,
};
